mapper.select:

select no, name, email, gender, role 
from member 
where email=#{email}


select no, name, email, gender, role
from member 
where no=#{no}

mapper.update:
		
		update member 
			set name = #{name},
		
		
		 
			passwd = password(#{passwd}),
		
		
		
		
		gender = #{gender}
		where no = #{no}
